async function main() {
    console.log("Seeding comprehensive duas...\n");
    const categories = await prisma.duaCategory.findMany();
    const catMap = new Map(categories.map((c) => [c.name, c.id]));
    let added = 0, skipped = 0;
    for (const dua of DUAS) {
        const categoryId = catMap.get(dua.category);
        if (!categoryId) {
            console.warn(`  Category not found: "${dua.category}"`);
            continue;
        }
        const existing = await prisma.dua.findFirst({ where: { titleEnglish: dua.titleEnglish, categoryId } });
        if (existing) {
            skipped++;
            continue;
        }
        await prisma.dua.create({ data: { categoryId, titleArabic: dua.titleArabic, titleEnglish: dua.titleEnglish, textArabic: dua.textArabic, textTranslit: dua.textTranslit, textEnglish: dua.textEnglish, reference: dua.reference } });
        console.log(`  + [${dua.category}] ${dua.titleEnglish}`);
        added++;
    }
    console.log(`\nDone — ${added} duas added, ${skipped} already existed`);
    const counts = await prisma.dua.groupBy({ by: ["categoryId"], _count: { id: true } });
    console.log("\nDuas per category:");
    for (const row of counts) {
        const cat = categories.find((c) => c.id === row.categoryId);
        console.log(`  ${cat?.name ?? row.categoryId}: ${row._count.id}`);
    }
}
main().catch((e) => { console.error(e); process.exit(1); }).finally(() => prisma.$disconnect());
